import React, { useEffect, useState } from 'react';
import axios from 'axios';

const styles = {
  container: { maxWidth: '720px', margin: '40px auto', padding: '20px', background: 'white', borderRadius: '8px', boxShadow: '0 2px 6px rgba(0,0,0,0.1)' },
  heading: { marginBottom: '12px' },
  field: { marginBottom: '8px', color: '#333' },
  btn: { padding: '10px 14px', background: '#dc3545', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }
};

export default function Profile() {
  const [user, setUser] = useState(null);
  const [err, setErr] = useState('');

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location = '/';
  };

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;
        const res = await axios.get('http://localhost:5000/api/auth/me', {
          headers: { 'x-auth-token': token }
        });
        setUser(res.data);
      } catch (error) {
        setErr('Could not fetch profile');
      }
    };
    fetchUser();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Profile</h2>
      {err && <div style={{ color: 'red' }}>{err}</div>}
      {user ? (
        <div>
          <div style={styles.field}><strong>Name:</strong> {user.name}</div>
          <div style={styles.field}><strong>Email:</strong> {user.email}</div>
          <div style={styles.field}><strong>Company:</strong> {user.companyName || '—'}</div>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
      <button style={styles.btn} onClick={handleLogout}>Logout</button>
    </div>
  );
}
