/**
 * client/src/components/CustomerAnalysis.js
 * AI-generated: example analytics component using Chart.js.
 * Human-read notes:
 * - Replace dummy aggregation with real business KPIs.
 * - Add loading and error states, and consider memoizing chart data.
 */
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  // Tooltip,
  Legend,
} from 'chart.js';
import api from '../utils/api';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const CustomerAnalysis = () => {
  const [analyticsData, setAnalyticsData] = useState(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        const res = await api.get('/jobs/analytics');
        setAnalyticsData(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchAnalytics();
  }, []);

  const chartData = {
    labels: analyticsData?.jobsByMonth.map(item => {
      const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
      return months[item._id - 1];
    }) || [],
    datasets: [
      {
        label: 'Jobs Posted',
        data: analyticsData?.jobsByMonth.map(item => item.count) || [],
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  return (
    <div className="card analytics-card">
      <h2 className="analytics-heading">Customer Analysis</h2>
      <div className="analytics-stats">
        <div className="stat-card">
          <h3 className="stat-heading">Total Jobs Posted</h3>
          <p className="stat-value">{analyticsData?.totalJobs || 0}</p>
        </div>
      </div>
      <div className="analytics-chart">
        <h3 className="chart-heading">Jobs Posted by Month</h3>
        <Line data={chartData} />
      </div>
    </div>
  );
};

export default CustomerAnalysis;
