import React, { useState } from 'react';
import api from '../utils/api';
import { useNavigate, Link } from 'react-router-dom';

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', password: '', companyName: '' });
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');
    try {
      const res = await api.post('/auth/register', form);
      if (res.data?.token) localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (error) {
      setErr(error.response?.data?.msg || 'Registration failed');
    }
  };

  return (
    <div className="card form">
      <h2 className="text-center">Create your account</h2>
      {err && <div className="form-error">{err}</div>}
      <form onSubmit={onSubmit} className="form-inner">
        <label className="form-label">Full Name</label>
        <input className="form-input" name="name" placeholder="Full Name" value={form.name} onChange={onChange} required />
        <label className="form-label">Company (optional)</label>
        <input className="form-input" name="companyName" placeholder="Company Name (optional)" value={form.companyName} onChange={onChange} />
        <label className="form-label">Email</label>
        <input className="form-input" name="email" placeholder="Email" value={form.email} onChange={onChange} required />
        <label className="form-label">Password</label>
        <input className="form-input" name="password" type="password" placeholder="Password" value={form.password} onChange={onChange} required autoComplete="new-password" />
        <button className="btn" type="submit">Create Account</button>
      </form>
      <div className="text-center form-footer">
        Already have an account? <Link to="/">Login</Link>
      </div>
    </div>
  );
}
