import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const styles = {
  container: { maxWidth: '480px', margin: '40px auto', padding: '20px', background: 'white', borderRadius: '8px', boxShadow: '0 2px 6px rgba(0,0,0,0.1)' },
  heading: { textAlign: 'center', marginBottom: '16px' },
  input: { width: '100%', padding: '10px', marginBottom: '12px', border: '1px solid #ddd', borderRadius: '4px' },
  button: { width: '100%', padding: '12px', background: '#28a745', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' },
  footer: { marginTop: '12px', textAlign: 'center' }
};

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', password: '', companyName: '' });
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/register', form);
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (error) {
      setErr(error.response?.data?.msg || 'Registration failed');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Register</h2>
      {err && <div style={{ color: 'red', marginBottom: '12px' }}>{err}</div>}
      <form onSubmit={onSubmit}>
        <input style={styles.input} name="name" placeholder="Full Name" value={form.name} onChange={onChange} required />
        <input style={styles.input} name="companyName" placeholder="Company Name (optional)" value={form.companyName} onChange={onChange} />
        <input style={styles.input} name="email" placeholder="Email" value={form.email} onChange={onChange} required />
        <input style={styles.input} name="password" type="password" placeholder="Password" value={form.password} onChange={onChange} required />
        <button style={styles.button} type="submit">Create Account</button>
      </form>
      <div style={styles.footer}>
        Already have an account? <Link to="/">Login</Link>
      </div>
    </div>
  );
}
