/**
 * client/src/components/Dashboard.js
 * AI-generated: dashboard layout with sidebar and job list.
 * Human-read notes:
 * - Replace window.location.reload usage with state-driven updates.
 * - Improve accessibility and keyboard navigation.
 */
import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import JobForm from './JobForm';

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    let mounted = true;
    const fetchJobs = async () => {
      try {
        const res = await api.get('/jobs/myjobs');
        if (mounted) setJobs(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchJobs();

    // Listen for job postings to refresh the list
    const onJobsUpdated = () => fetchJobs();
    window.addEventListener('jobsUpdated', onJobsUpdated);

    return () => {
      mounted = false;
      window.removeEventListener('jobsUpdated', onJobsUpdated);
    };
  }, []);

  return (
    <div className="container">
      <div className="dashboard">
        <aside className="sidebar">
          <div className="card">
            <nav>
              <ul style={{listStyle:'none',padding:12}}>
                <li style={{marginBottom:10}}><a className="nav-link" href="/dashboard">Jobs</a></li>
                <li style={{marginBottom:10}}><a className="nav-link" href="/profile">Profile</a></li>
                <li style={{marginBottom:10}}><a className="nav-link" href="/analysis">Customer Analysis</a></li>
                <li style={{marginTop:12}}><button className="btn secondary" onClick={() => {
                  localStorage.removeItem('token');
                  window.location = '/';
                }}>Logout</button></li>
              </ul>
            </nav>
          </div>
        </aside>

        <main className="main">
          <h2>Job Postings</h2>
          <JobForm />
          <div className="jobs-grid">
            {jobs.map(job => (
              <div key={job._id} className="job-card">
                <h3 style={{ marginBottom: '10px' }}>{job.jobTitle}</h3>
                <p style={{ color: '#444', marginBottom: '15px' }}>{job.jobDescription}</p>
                <div style={{display:'flex',justifyContent:'space-between',color:'#666'}}>
                  <span>{job.companyName}</span>
                  <span>{job.location}</span>
                  <span>{new Date(job.deadline).toLocaleDateString()}</span>
                </div>
              </div>
            ))}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
