/**
 * client/src/components/Login.js
 * AI-generated: simple login form that POSTs to /api/auth/login.
 * Human-read notes:
 * - Add better form validation and loading states
 * - Consider storing JWT in httpOnly cookie instead of localStorage
 */
import React, { useState } from 'react';
import api from '../utils/api';
import { useNavigate, Link } from 'react-router-dom';

const styles = {
  container: { maxWidth: '420px', margin: '40px auto', padding: '20px', background: 'white', borderRadius: '8px', boxShadow: '0 2px 6px rgba(0,0,0,0.1)' },
  heading: { textAlign: 'center', marginBottom: '16px' },
  input: { width: '100%', padding: '10px', marginBottom: '12px', border: '1px solid #ddd', borderRadius: '4px' },
  button: { width: '100%', padding: '12px', background: '#70767cff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' },
  footer: { marginTop: '12px', textAlign: 'center' }
};

export default function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setErr('');
    try {
      const res = await api.post('/auth/login', form);
      // server sets httpOnly cookie; also store token for compatibility with existing code
      if (res.data?.token) localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (error) {
      setErr(error.response?.data?.msg || 'Login failed');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Login</h2>
      {err && <div style={{ color: 'red', marginBottom: '12px' }}>{err}</div>}
      <form onSubmit={onSubmit}>
        <input style={styles.input} name="email" placeholder="Email" value={form.email} onChange={onChange} required />
        <input style={styles.input} name="password" type="password" placeholder="Password" value={form.password} onChange={onChange} required />
        <button style={styles.button} type="submit">Sign In</button>
      </form>
      <div style={styles.footer}>
        Don't have an account? <Link to="/register">Register</Link>
      </div>
    </div>
  );
}
