// 

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const [open, setOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location = '/';
  };

  return (
    <>
      <style>{`
        .site-header {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 14px 24px;
          background: #ffffff;
          color: #222;
          position: sticky;
          top: 0;
          z-index: 1000;
          box-shadow: 0 2px 8px rgba(0,0,0,0.1);
        }
        .brand {
          font-size: 20px;
          font-weight: 700;
        }
        .nav-links {
          display: flex;
          align-items: center;
          gap: 16px;
        }
        .nav-link {
          text-decoration: none;
          font-size: 16px;
          font-weight: 500;
          color: #222;
          transition: 0.3s;
        }
        .nav-link:hover {
          color: #4f46e5;
        }
        .btn {
          border: none;
          cursor: pointer;
          padding: 6px 12px;
          border-radius: 6px;
          font-size: 15px;
          font-weight: 500;
        }
        .btn.secondary {
          background: #f3f4f6;
          color: #222;
          transition: 0.3s;
        }
        .btn.secondary:hover {
          background: #e2e6ea;
        }
        .mobile-toggle {
          display: none;
          font-size: 24px;
          background: none;
          border: none;
          color: #222;
          cursor: pointer;
        }

        @media (max-width: 768px) {
          .mobile-toggle {
            display: block;
          }
          .nav-links {
            position: absolute;
            top: 64px;
            right: 0;
            background: #ffffff;
            flex-direction: column;
            width: 200px;
            padding: 16px;
            border-radius: 0 0 0 10px;
            box-shadow: -2px 5px 10px rgba(0,0,0,0.15);
            transform: translateX(110%);
            opacity: 0;
            transition: 0.3s ease-in-out;
          }
          .nav-links.open {
            transform: translateX(0);
            opacity: 1;
          }
        }
      `}</style>

      <header className="site-header">
        <div className="brand">Job Portal</div>
        <div>
          <button className="mobile-toggle" onClick={() => setOpen(v => !v)}>â˜°</button>
          <nav className={`nav-links ${open ? 'open' : ''}`}>
            <Link className="nav-link" to="/dashboard" onClick={() => setOpen(false)}>Dashboard</Link>
            <Link className="nav-link" to="/analysis" onClick={() => setOpen(false)}>Analysis</Link>
            <Link className="nav-link" to="/profile" onClick={() => setOpen(false)}>Profile</Link>
            <button onClick={handleLogout} className="btn secondary">Logout</button>
          </nav>
        </div>
      </header>
    </>
  );
}
