/**
 * client/src/components/Navbar.js
 * AI-generated navbar. Human-read notes: make links conditional based on auth state and improve mobile layout.
 */
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '12px 20px',
    backgroundColor: '#939699ff',
    color: 'white'
  },
  brand: {
    fontWeight: 'bold',
    fontSize: '18px'
  },
  navLinks: {
    display: 'flex',
    gap: '12px'
  },
  link: {
    color: 'white',
    textDecoration: 'none'
  }
};

export default function Navbar() {
  const [open, setOpen] = useState(false);
  const [theme, setTheme] = useState(() => {
    try { return localStorage.getItem('theme') || 'light' } catch { return 'light' }
  });
  useEffect(() => {
    if (theme === 'dark') document.body.classList.add('dark');
    else document.body.classList.remove('dark');
    try { localStorage.setItem('theme', theme) } catch {}
  }, [theme]);
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location = '/';
  };

  return (
    <header className="site-header">
      <div style={{display:'flex',alignItems:'center',gap:12}}>
        <div className="brand">Job Portal</div>
      </div>
      <div>
        <button className="mobile-toggle" onClick={() => setOpen(v => !v)} aria-label="Toggle menu">‚ò∞</button>
        <nav className={`nav-links ${open ? 'open' : ''}`}>
          <Link className="nav-link" to="/dashboard" onClick={() => setOpen(false)}>Dashboard</Link>
          <Link className="nav-link" to="/analysis" onClick={() => setOpen(false)}>Analysis</Link>
          <Link className="nav-link" to="/profile" onClick={() => setOpen(false)}>Profile</Link>
          <button onClick={() => setTheme(t => t === 'dark' ? 'light' : 'dark')} className="btn secondary" style={{marginRight:8}} aria-label="Toggle theme">{theme === 'dark' ? 'üåô' : '‚òÄÔ∏è'}</button>
          <button onClick={handleLogout} className="btn secondary">Logout</button>
        </nav>
      </div>
    </header>
  );
}
