/**
 * client/src/components/JobForm.js
 * AI-generated: job posting form. Sends POST /api/jobs using stored x-auth-token.
 * Human-read notes:
 * - Add client-side validation, better UX on submit, avoid full page reloads.
 */
import React, { useState } from 'react';
import api from '../utils/api';

const JobForm = () => {
  const [formData, setFormData] = useState({
    jobTitle: '',
    jobDescription: '',
    companyName: '',
    location: '',
    deadline: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/jobs', formData);
      // Reset form
      setFormData({
        jobTitle: '',
        jobDescription: '',
        companyName: '',
        location: '',
        deadline: ''
      });
      // Notify other components to refresh job listings
      window.dispatchEvent(new Event('jobsUpdated'));
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="card">
      <h3 className="text-center">Post a New Job</h3>
      <form onSubmit={handleSubmit} className="form">
        <label className="form-label">Job Title</label>
        <input className="form-input" type="text" name="jobTitle" placeholder="Job Title" value={formData.jobTitle} onChange={handleChange} required />

        <label className="form-label">Job Description</label>
  <textarea className="form-input textarea-large" name="jobDescription" placeholder="Job Description" value={formData.jobDescription} onChange={handleChange} required />

        <label className="form-label">Company Name</label>
        <input className="form-input" type="text" name="companyName" placeholder="Company Name" value={formData.companyName} onChange={handleChange} />

        <label className="form-label">Location</label>
        <input className="form-input" type="text" name="location" placeholder="Location" value={formData.location} onChange={handleChange} />

        <label className="form-label">Deadline</label>
        <input className="form-input" type="date" name="deadline" value={formData.deadline} onChange={handleChange} />

  <button className="btn" type="submit">Post Job</button>
      </form>
    </div>
  );
};

export default JobForm;
